import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    rememberMe: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate login - in real app, this would make an API call
    onLogin({ username: formData.username, role: 'admin' });
  };

  return (
    <div className="auth-container">
      <div className="auth-left">
        {/* Background image placeholder */}
      </div>
      <div className="auth-right">
        <div className="auth-form">
          <div style={{ textAlign: 'right', marginBottom: '20px' }}>
            <button style={{ background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer' }}>
              â˜°
            </button>
          </div>
          
          <h1 className="auth-title">Welcome Back!</h1>
          <p className="auth-subtitle">login to continue</p>
          
          <form onSubmit={handleSubmit}>
            <div className="auth-form-group">
              <label className="auth-label" htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                name="username"
                className="auth-input"
                value={formData.username}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="auth-form-group">
              <label className="auth-label" htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                name="password"
                className="auth-input"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="auth-options">
              <div className="auth-checkbox">
                <input
                  type="checkbox"
                  id="rememberMe"
                  name="rememberMe"
                  checked={formData.rememberMe}
                  onChange={handleChange}
                />
                <label htmlFor="rememberMe">Remember me</label>
              </div>
              <a href="#" className="auth-link">Forgot Password?</a>
            </div>
            
            <button type="submit" className="auth-button">
              Login now
            </button>
          </form>
          
          <div className="auth-switch">
            Don't have an account? <Link to="/signup" className="auth-switch-link">Sign up</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
